define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Log in to the administration UI
         */
        var login = function() {
            oae.api.authentication.localLogin($('#adminlogin-signin-local-form-username', $rootel).val(), $('#adminlogin-signin-local-form-password', $rootel).val(), function(err) {
                if (err) {
                    // Show the error message
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__LOGIN_FAILED__', 'adminlogin'),
                        oae.api.i18n.translate('__MSG__CHECK_LOGIN_CREDENTIALS__', 'adminlogin'),
                        'error');
                } else {
                    window.location.reload(true);
                }
            });
            return false;
        };

        /**
         * Render the external and local authentication strategies
         *
         * @param  {Tenant}    currentContext    Tenant object representing the tenant for which the administration UI is currently being shown
         */
        var renderLogin = function(currentContext) {
            oae.api.util.template().render($('#adminlogin-external-authentication-template', $rootel), {
                'currentContext': currentContext,
                'enabledStrategies': oae.api.authentication.getEnabledStrategies(),
                'redirectUrl': window.location.pathname
            }, $('#adminlogin-authentication-container', $rootel));
        };

        /**
         * Initialize the adminlogin widget
         */
        var initLogin = function() {
            $(document).on('oae.context.send.adminlogin', function(ev, data) {
                renderLogin(data.currentContext);
            });

            $(document).trigger('oae.context.get', 'adminlogin');

            // Set up the form validation
            oae.api.util.validation().validate($('#adminlogin-signin-local-form'), {'submitHandler': login});
        };

        initLogin();

    };
});
