define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        // Cache the current context
        var currentContext = null;

        // Cache all tenant data
        var allTenants = null;

        /**
         * Log out of the administration interface
         */
        var doLogout = function() {
            oae.api.authentication.logout(function() {
                window.location.reload(true);
            });
        };

        /**
         * Render the administration header
         */
        var renderHeader = function() {
            // Render the footer
            oae.api.util.template().render($('#header-template', $rootel), {
                'context': currentContext,
                'tenants': allTenants
            }, $('#header-container', $rootel));
        };

        /**
         * Initialize the header widget
         */
        var initHeader = function() {
            $(document).trigger('oae.context.get', 'header');

            $(document).on('oae.context.send.header', function(ev, data) {
                currentContext = data.currentContext;
                allTenants = data.allTenants;
                renderHeader();
            });

            // We're logged in, bind the logout handler
            $rootel.on('click', '#header-user-logout', doLogout);
        };

        initHeader();

    };
});
