define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Log in to the administration interface
         */
        var doLogin = function() {
            oae.api.authentication.localLogin($('#login-form-username', $rootel).val(), $('#login-form-password', $rootel).val(), function(err) {
                if (err) {
                    // Show the error message
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__LOGIN_FAILED__', 'login'),
                        oae.api.i18n.translate('__MSG__CHECK_LOGIN_CREDENTIALS__', 'login'),
                        'error');
                } else {
                    window.location.reload(true);
                }
            });
            return false;
        };

        /**
         * Initialize the login widget
         */
        var initLogin = function() {
            $('#login-container').show();
            oae.api.util.validation().validate($('#login-form'), {'submitHandler': doLogin});
        };

        initLogin();

    };
});
