 define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Render the configured Addendum link
         */
        var renderAddendumLink = function() {
            // Get the configured addendum URL
            var url = oae.api.config.getValue('oae-tickets', 'general', 'addendumUrl');
            oae.api.util.template().render($('#oa-addendum-link-template', $rootel), {
                'addendumUrl': url
            }, $('#oa-addendum-link-container', $rootel));
        };

        /**
         * Binds actions to various elements in the terms and conditions widget
         */
        var addBinding = function() {
            // Show the Cambridge Addendum modal
            $(document).on('click', '.oa-trigger-addendum', function() {
                $('#oa-addendum-modal', $rootel).modal();
            });
        };

        renderAddendumLink();
        addBinding();
    };
});
