 define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Render the configured Addendum link
         */
        var renderAddendumLink = function() {
            // Get the configured addendum URL
            var url = oae.api.config.getValue('oae-publications', 'general', 'addendumUrl');
            oae.api.util.template().render($('#oa-addendumlink-template', $rootel), {
                'addendumUrl': url
            }, $('#oa-addendumlink-container', $rootel));
        };

        renderAddendumLink();
    };
});
