define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        $rootel = $('#' + uid);
        oae.api.util.template().render($('#oa-authentication-template', $rootel), {}, $('#oa-authentication-container', $rootel));

        $rootel.on('click', '.js-btn-logout', function() {
            oae.api.authentication.logout(function() {
                window.location = '/';
            });
        })

    };

});