define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Binds actions to various elements in the terms and conditions widget
         */
        var addBinding = function() {
            // Show the share modal
            $(document).on('click', '.oa-trigger-modalshare', function() {
                renderLink($(this).data('share-link'));
                $('#oa-modalshare-modal', $rootel).modal();
            });

            $(document).on('focus', '.oa-modalshare-sharelink', function() {
                _.defer(selectLink);
            });

            $('#oa-modalshare-modal', $rootel).on('shown.bs.modal', function() {
                $('.oa-modalshare-sharelink').focus();
            });
        };

        /**
         * Render the link in the modal window
         *
         * @param  {String}    shareLink       The link to be shown in the modal
         */
        var renderLink = function(shareLink) {
            oae.api.util.template().render($('#oa-modalshare-link-template'), {
                'shareLink': shareLink
            }, $('#oa-modalshare-link-container'));
        };

        /**
         * Text-select the link
         */
        var selectLink = window.selectLink = function() {
            $('.oa-modalshare-sharelink')[0].select();
        };

        addBinding();
    };
});
