 define(['jquery', 'oae.core', 'jquery.fileupload'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        var $upload;
        var $contentContainer = $('#oa-uploadnewversion-content-container');
        var selectedFile;
        var contentId;

        /**
         * Initialise jQuery.fileupload on the file upload form
         */
        var initFileUpload = function(url) {
            $upload.fileupload({
                'forceIframeTransport': !$.support.xhrFileUpload && !$.support.xhrFormDataFileUpload,
                'replaceFileInput': false,
                'add': function(event, data) {
                    selectedFile = data.files[0];
                    $('#oa-modaluploadnewversion-btn-submit', $rootel).prop('disabled', selectedFile === undefined);
                },
                'url': url,
                'progress': function(data) {
                    var percentage = data.loaded / data.total * 100;
                    var totalSize = $.fn.fileSize(data.total);
                    var doneSize = $.fn.fileSize(data.loaded);
                    // Update the upload progress bar
                    var $progressBar = $('.oa-progress-bar', $rootel).width(percentage + '%').attr('aria-valuenow', percentage);
                    $('span', $progressBar).text(percentage + '% Complete');
                    // Update the progress text (e.g. '1.2 MB of 5 MB')
                    $('.oa-progress-size', $rootel).text(doneSize + ' of ' + totalSize);
                }
            });
        };

        /**
         * Handler triggered when the form has been submitted
         */
        var onUploadSubmit = function(evt) {
            uploadNewVersion();
            evt.preventDefault();
        };

        /**
         * Upload what's currently selected in the file input field as the new version of the contentItem
         */
        var uploadNewVersion = function() {
            oae.api.content.uploadNewVersion($upload, selectedFile, function(error) {
                if (error) {
                    renderError();
                    return;
                }
                window.location.reload();
            });
            renderProgress();
        };

        /**
         * Generate the upload new version url for a given content id
         *
         * @param  {String}    id    The id of the content item to be updated
         */
        var getNewVersionUrlForContentId = function(id) {
            return '/api/content/' + id + '/newversion';
        };

        /**
         * Render the error template
         */
        var renderError = function() {
            oae.api.util.template().render($('#oa-uploadnewversion-error-template'), null, $contentContainer);
        };

        /**
         * Render the form template
         */
        var renderForm = function() {
            oae.api.util.template().render($('#oa-uploadnewversion-form-template'), null, $contentContainer);
        };

        /**
         * Render the upload progress template
         */
        var renderProgress = function() {
            oae.api.util.template().render($('#oa-uploadnewversion-progress-template'), {
                'fileName': selectedFile.name
            }, $contentContainer);
        };

        /**
         * Binds actions to various elements in the terms and conditions widget
         */
        var addBinding = function() {
            // Show the Cambridge Addendum modal
            $(document).on('click', '.oa-trigger-modaluploadnewversion', function() {
                renderForm();
                $upload = $('form', $rootel).on('submit', onUploadSubmit);
                initFileUpload(getNewVersionUrlForContentId($(this).data('content-id')));
                $('#oa-modaluploadnewversion-modal', $rootel).modal();
            });
        };

        addBinding();
    };
});
