define(['jquery', 'oae.core'], function($, oae) {

    return function(uid, showSettings, widgetData) {

        // The widget container
        var $rootel = $('#' + uid);
        var content = widgetData.content;

        var renderFileInfo = function() {
            oae.api.util.template().render($('#oa-publicationfile-template', $rootel), {
                'fileName': content.displayName,
                'fileSize': content.size ? $.fn.fileSize(content.size) : '',
                'fileDownload': content.downloadPath,
                'createdBy': content.createdBy,
                'showActions': widgetData.showActions
            }, $('#oa-publicationfile-container', $rootel));
        };

        renderFileInfo();
    };
});
