 define(['jquery', 'oae.core', 'markdown'], function($, oae) {

    return function(uid) {
        var $rootel = $('#' + uid);

        /**
         * Fetch the Terms and Conditions and render the parsed markdown into the widget.
         */
        var renderTermsAndConditions = function() {
            oae.api.user.getTC(function(err, data) {
                // Render the modal body
                oae.api.util.template().render($('#oa-terms-modal-body-template', $rootel), {
                    'terms': data.text
                }, $('#oa-terms-modal-body-container', $rootel));
            });
        };

        /**
         * Binds actions to various elements in the terms and conditions widget
         */
        var addBinding = function() {
            // Show the terms and conditions modal
            $(document).on('click', '.oa-trigger-terms', function() {
                $('#oa-terms-modal', $rootel).modal();
            });
        };

        renderTermsAndConditions();
        addBinding();
    };
});
