casper.test.begin('Widget - About Content', function(test) {

    /**
     * Open the about content modal with assertions
     */
    var verifyOpenAboutContent = function() {
        casper.waitForSelector('#content-clip-container .oae-clip-content > button', function() {
            casper.click('#content-clip-container .oae-clip-content > button');
            test.assertExists('.oae-trigger-aboutcontent', 'About content trigger exists');
            casper.click('.oae-trigger-aboutcontent');
            casper.waitUntilVisible('#aboutcontent-modal', function() {
                test.assertVisible('#aboutcontent-modal', 'About content pane is showing after trigger');
                casper.click('#content-clip-container .oae-clip-content > button');
            });
        });
    };

    /**
     * Verify that all elements are present in the about content modal
     *
     * @param {User}    user1    The first test user profile object
     */
    var verifyAboutContentElements = function(user1) {
        test.assertExists('#aboutcontent-modal .modal-header h3', 'Verify that the modal has a header');
        test.assertSelectorHasText('#aboutcontent-modal .modal-header h3', 'About', 'Verify that the modal header reads \'About\'');
        test.assertExists('#aboutcontent-modal .modal-body ul.oae-list li', 'Verify that the modal shows who added the content');
        test.assertExists('#aboutcontent-modal .modal-body ul.oae-list li .oae-listitem-primary-thumbnail', 'Verify that the modal shows the picture of the user who added the content');
        test.assertExists('#aboutcontent-modal .modal-body ul.oae-list li .oae-listitem-metadata h3', 'Verify that the modal shows the name of the user who added the content');
        test.assertSelectorHasText('#aboutcontent-modal .modal-body ul.oae-list li .oae-listitem-metadata h3', user1.displayName, 'Verify that the name correct name is shown');
        test.assertExists('#aboutcontent-modal .modal-body ul.oae-list li .oae-listitem-metadata small', 'Verify that the modal shows the tenant of the user who added the content');
        test.assertSelectorHasText('#aboutcontent-modal .modal-body ul.oae-list li .oae-listitem-metadata small', 'CasperJS Tenant', 'Verify that the metadata shows the tenant name');
        test.assertExists('#aboutcontent-modal .modal-body #aboutcontent-description-container #aboutcontent-title', 'Verify that the modal shows title of the content');
        test.assertSelectorHasText('#aboutcontent-modal .modal-body #aboutcontent-description-container #aboutcontent-title', 'balloons.jpg', 'Verify that the correct content title is shown');
        test.assertExists('#aboutcontent-modal .modal-body #aboutcontent-description-container #aboutcontent-description', 'Verify that the modal shows description of the content');
        test.assertSelectorHasText('#aboutcontent-modal .modal-body #aboutcontent-description-container #aboutcontent-description', 'Test description', 'Verify that the correct content description is shown');
    };

    casper.start(configUtil().tenantUI, function() {
        // Create a couple of users to test with
        var user1 = null;
        var user2 = null;
        userUtil().createUsers(2, function(users) {
            user1 = users[0];
            user2 = users[1];
        });

        // Login with the first user
        casper.then(function() {
            userUtil().doLogIn(user1.username, configUtil().defaultUserPassword);
        });

        var contentURL = null;
        casper.then(function() {
            casper.echo('Verify open about content modal', 'INFO');
            contentUtil().createFile(null, null, [user2.id], function(_contentURL) {
                contentURL = configUtil().tenantUI + _contentURL;
                var contentId = _contentURL.split('/');
                contentId = 'c:test:' + contentId[contentId.length - 1];

                contentUtil().updateContent(contentId, {'description': 'Test description'}, function() {
                    casper.then(function() {
                        userUtil().doLogOut();
                    });

                    casper.then(function() {
                        userUtil().doLogIn(user2.username, configUtil().defaultUserPassword);
                    });

                    casper.thenOpen(contentURL, function() {
                        casper.waitForSelector('#content-clip-container .oae-clip-content > button', function() {
                            verifyOpenAboutContent();
                        });
                    });
                });
            });
        });

        casper.then(function() {
            casper.echo('Verify about content elements', 'INFO');
            verifyAboutContentElements(user1);
        });

        casper.then(function() {
            userUtil().doLogOut();
        });

    });

    casper.run(function() {
        test.done();
    });
});
