define(['jquery', 'oae.core', 'oae.api.config'], function($, oae, configAPI) {
    return function(uid) {

        // The widget container
        $rootel = $('#' + uid);

        // URL for feedback and support
        var feedbackUrl = null;

        // If UserVoice enabled, use redirect API for URL
        if (configAPI.getValue('oae-uservoice', 'general', 'enabled')) {
            feedbackUrl = '/api/uservoice/redirect';
        }

        // Should Terms and Conditions be shown
        var showTerms = oae.api.config.getValue('oae-principals', 'termsAndConditions', 'enabled');

        // Render the template
        oae.api.util.template().render($('#footer-template', $rootel), {
            'date': new Date().getFullYear(),
            'feedbackUrl': feedbackUrl,
            'showTerms': showTerms
        }, $('#footer-container', $rootel));
    };
});
