define(['jquery', 'oae.core', 'jquery.autoSuggest'], function($, oae) {

    return function(uid, showSettings) {

        // The widget container
        var $rootel = $('#' + uid);

        // Caches the group profile
        var groupData = null;

        // Variable that will be used to keep track of the current infinite scroll instance
        var infinityScroll = null;

        /**
         * Appends members to the list that were selected through the autosuggest.
         *
         * @param  {Object[]}    members    An array of objects containing the id and displayName of the principals to share with
         */
        var appendNewMembers = function(members) {
            // Add the members to the list or update their role value if they're already in the list
            $.each(members.results, function(i, newMember) {
                var isAlreadyMember = false;
                $.each(groupData.members.results, function(ii, member) {
                    // If the newMember is already a member, only update the role
                    if (member.profile.id === newMember.profile.id) {
                        member.role = newMember.role;
                        member.deleted = false;
                        isAlreadyMember = true;
                    }
                });
                if (!isAlreadyMember) {
                    groupData.members.results.push(newMember);
                }
            });
            oae.api.util.template().render($('#grouppermissions-members-template', $rootel), groupData.members, $('.oae-list', $rootel));
        };

        /**
         * Retrieves the members of the group and initializes the infinite scroll plugin
         * that will take care of handling paging and rendering
         */
        var getMembers = function() {
            // Disable the previous infinite scroll
            if (infinityScroll) {
                infinityScroll.kill();
            }

            var url = '/api/group/' + groupData.id + '/members';

            // Set up the infinite scroll instance
            infinityScroll = $('.oae-list', $rootel).infiniteScroll(url, {
                'limit': 12
            }, '#grouppermissions-members-template', {
                'postProcessor': function(data) {
                    groupData.members = data;
                    return data;
                }
            });
        };

        /**
         * Removes a member from the list and marks it as deleted in the cached group data.
         * When the group permissions are saved the data will be persisted.
         */
        var deleteMember = function() {
            var principalId = $(this).attr('data-id');

            // Remove the list item
            $(this).parents('li').remove();

            // Mark the member as deleted
            $.each(groupData.members.results, function(i, member) {
                if (member.profile.id === principalId) {
                    groupData.members.results[i].deleted = true;
                    return false;
                }
            });
        };

        /**
         * Updates the role of a member.
         * When the group permissions are saved the data will be persisted.
         */
        var updateRole = function() {
            var principalId = $(this).attr('data-id');
            var selectedRole = $(this).val();

            // Update the role of the member
            $.each(groupData.members.results, function(i, member) {
                if (member.profile.id === principalId) {
                    groupData.members.results[i].role = selectedRole;
                    return false;
                }
            });
        };

        /**
         * Saves the visibility setting and shares the group with other members
         */
        var saveGroupPermissions = function() {
            // Update the members of the group
            var members = {};
            $.each(groupData.members.results, function(i, member) {
                members[member.profile.id] = member.deleted ? false : member.role;
            });
            oae.api.group.setGroupMembers(groupData.id, members, function(membersErr) {
                // Update the group visibility
                oae.api.group.updateGroup(groupData.id, {
                    'visibility': groupData.visibility
                }, function(visibilityErr) {
                    // Show a failure notification if the members or visibility could not be saved
                    if (visibilityErr || membersErr) {
                        oae.api.util.notification(
                            oae.api.i18n.translate('__MSG__GROUP_PERMISSIONS_NOT_SAVED__', 'grouppermissions'),
                            oae.api.i18n.translate('__MSG__GROUP_PERMISSIONS_COULD_NOT_BE_SAVED__', 'grouppermissions'),
                            'error'
                        );
                    // Show a success notification and close the dialog when the members and visibility have been updated
                    } else {
                        oae.api.util.notification(
                            oae.api.i18n.translate('__MSG__GROUP_PERMISSIONS_SAVED__', 'grouppermissions'),
                            oae.api.i18n.translate('__MSG__GROUP_PERMISSIONS_SUCCESSFULLY_SAVED__', 'grouppermissions'),
                            'info'
                        );
                        $(window).trigger('done.grouppermissions.oae');
                        $('#grouppermissions-modal', $rootel).modal('hide');
                    }
                });
            });
        };


        /////////////////
        // AUTOSUGGEST //
        /////////////////

        /**
         * Initializes the autosuggest used for sharing with other users or groups.
         * TODO: The Autosuggest initiation needs to be replaced by the API function once merged.
         */
        var setUpAutoSuggest = function() {
            // Initialize the autoSuggest field
            $('#grouppermissions-share-autosuggest', $rootel).autoSuggest('/api/search/general', {
                'selectedItemProp': 'displayName',
                'searchObjProps': 'displayName',
                'selectedValuesProp': 'id',
                'extraParams': {
                    'resourceTypes': ['user', 'group']
                },
                'minChars': 3,
                'neverSubmit': true,
                'showResultListWhenNoMatch': true,
                'retrieveLimit': 5,
                'retrieveComplete': function(data) {
                    // TODO: Remove this once the API function for the autosuggest
                    // is in place. That will be taking care of potential XSS issues.
                    $.each(data.results, function(index, result) {
                        result.displayName = oae.api.util.security().encodeForHTML(result.displayName);
                    });
                    return data.results;
                }
            });
        };

        /**
         * Loops over the selected principals in the autosuggest field and returns them in an Array.
         *
         * @return {Object[]}   members   An array of objects containing the id and displayName of the principals to share with
         */
        var getAutosuggestSelection = function() {
            var members = {
                'results': []
            };
            $.each($rootel.find('.as-selections > li'), function(index, selection) {
                var id = $(selection).attr('data-value');
                // jQuery autosuggest will always prepare an empty item for the next item that needs to be
                // added to the list. Therefore, it is possible that an item in the list is empty
                if (id) {
                    members.results.push({
                        'profile': {
                            'displayName': $(selection).data().data && $(selection).data().data.displayName,
                            'id': id
                        },
                        'role': $('#grouppermissions-share-role', $rootel).val()
                    });
                }
            });
            return members;
        };


        /////////////////////
        // VIEW MANAGEMENT //
        /////////////////////

        /**
         * [ description]
         *
         * @returns {[type]} [description]
         */
        var showVisibility = function() {
            // Show the visibility container and footer
            $('.modal-body > div', $rootel).hide();
            $('#grouppermissions-visibility', $rootel).show();
            $('.modal-footer > div', $rootel).hide();
            $('#grouppermissions-visibility-footer', $rootel).show();
        };

        /**
         * Renders the visibility view of the group permissions.
         * Includes an autosuggest to select new members to add to the group.
         */
        var renderVisibility = function() {
            oae.api.util.template().render($('#grouppermissions-visibility-template', $rootel), {
                'groupData': groupData
            }, $('#grouppermissions-visibility', $rootel));
        };

        /**
         * [ description]
         *
         * @returns {[type]} [description]
         */
        var showShare = function() {
            // Show the share container and footer
            $('.modal-body > div', $rootel).hide();
            $('#grouppermissions-share', $rootel).show();
            $('.modal-footer > div', $rootel).hide();
            $('#grouppermissions-share-footer', $rootel).show();
        };

        /**
         * Renders the share view of the group permissions.
         * Includes an autosuggest to select new members to add to the group.
         */
        var renderShare = function() {
            oae.api.util.template().render($('#grouppermissions-share-template', $rootel), {
                'groupData': groupData
            }, $('#grouppermissions-share', $rootel));

            setUpAutoSuggest();
        };

        /**
         * [ description]
         *
         * @returns {[type]} [description]
         */
        var showOverview = function() {
            // Show the overview container and footer
            $('.modal-body > div', $rootel).hide();
            $('#grouppermissions-overview', $rootel).show();
            $('.modal-footer > div', $rootel).hide();
            $('#grouppermissions-overview-footer', $rootel).show();
        };

        /**
         * [ description]
         *
         * @returns {[type]} [description]
         */
        var renderVisibilityOverview = function() {
            oae.api.util.template().render($('#grouppermissions-overview-visibility-template', $rootel), {
                'groupData': groupData
            }, $('#grouppermissions-overview-visibility', $rootel));
        };

        /**
         * [ description]
         *
         * @returns {[type]} [description]
         */
        var renderSharedOverview = function() {
            oae.api.util.template().render($('#grouppermissions-overview-shared-template', $rootel), {
                'groupData': groupData
            }, $('#grouppermissions-overview-shared', $rootel));
        };

        /**
         * Renders the general overview of the group permissions.
         * Includes group visibility and members.
         */
        var renderOverview = function() {
            renderVisibilityOverview();
            renderSharedOverview();
        };


        //////////////////////
        // DEINITIALIZATION //
        //////////////////////

        /**
         * Resets the widget to its initial state
         */
        var reset = function() {
            // Show the overview container and footer
            $('.modal-body > div', $rootel).hide();
            $('#grouppermissions-overview', $rootel).show();
            $('.modal-footer > div', $rootel).hide();
            $('#grouppermissions-overview-footer', $rootel).show();
        };


        ////////////////////
        // INITIALIZATION //
        ////////////////////

        /**
         * Sets up the group permissions modal
         */
        var setUpModal = function() {
            $(document).on('click', '.oae-trigger-grouppermissions', function() {
                $('#grouppermissions-modal', $rootel).modal();
                $(document).trigger('oae.context.get', 'grouppermissions');
            });

            $(document).on('oae.context.send.grouppermissions', function(ev, data) {
                groupData = data;
                renderOverview();
                getMembers();
            });

            $('#grouppermissions-modal', $rootel).on('hidden', reset);
        };

        /**
         * Binds actions to various elements in the group permissions widget
         */
        var addBinding = function() {
            // Render the visibility container when clicking 'change' in the visibility well
            $rootel.on('click', '#grouppermissions-change-visibility', function() {
                renderVisibility();
                showVisibility();
            });

            // Update the overview container when saving the visibility setting
            $rootel.on('click', '#grouppermissions-save-visibility', function() {
                // Update the visibility in the group data
                groupData.visibility = $('.oae-visibility-choice-container input[type="radio"]:checked', $rootel).val();
                renderVisibilityOverview();
                showOverview();
            });

            // Render the share container when clicking 'add more' at the bottom of the list of members
            $rootel.on('click', '#grouppermissions-share-add-more', function() {
                renderShare();
                showShare();
            });

            // Update the overview container when saving the autosuggest values
            $rootel.on('click', '#grouppermissions-share-update', function() {
                appendNewMembers(getAutosuggestSelection());
                showOverview();
            });

            // Catch changes in the visibility radio group
            $rootel.on('change', '.oae-visibility-choice-container input[type="radio"]', function() {
                $('.oae-visibility-choice', $rootel).removeClass('checked');
                $(this).parents('.oae-visibility-choice').addClass('checked');
            });

            // Delete a member from the list
            $rootel.on('click', '.oae-listitem-actions .close', deleteMember);

            // Update a member's role in the list
            $rootel.on('change', '.oae-listitem-actions select', updateRole);

            // Cancel the editing of visibility or members and go back to the overview
            $rootel.on('click', '.grouppermissions-cancel', showOverview);

            // Save the visibility and members
            $rootel.on('click', '#grouppermissions-save', saveGroupPermissions);
        };

        setUpModal();
        addBinding();
    };
});
