define(['jquery', 'oae/api/oae.core'], function($, oae) {

    return function(uid, showSettings) {

        var $rootel = $('#' + uid);

        var renderLibrary = function(err, data) {
            oae.api.util.renderTemplate('#library_template', {
                'library': data.results
            }, $('.oae-list', $rootel));
        };

        var getLibrary = function(page) {
            oae.api.content.getLibrary('u:camtest:VPABNJmWROJ', page, null, renderLibrary)
        };

        var addBinding = function() {
            $rootel.on('click', '.oae-puzzle-border', function() {
                $(this).parent().toggleClass('active');
            });

            $('#library_switch_view').on('click', function() {
                $rootel.find('.oae-list').toggleClass('grid');
            });
        };

        var doInit = function() {
            addBinding();
            getLibrary();
            $('.library_widget').show();
        };

        doInit();
    };
});
