define(['jquery', 'oae/api/oae.core'], function($, oae) {

    return function(uid, showSettings) {

        var $rootel = $('#' + uid);

        var renderMemberships = function(err, data) {
            oae.api.util.renderTemplate('#memberships_template', {
                'memberships': data.results
            }, $('.oae-list', $rootel));
        };

        var getMemberships = function(page) {
            oae.api.group.memberOf('u:camtest:VP8szOC4xi5', page, null, renderMemberships)
        };

        var addBinding = function() {
            $rootel.on('click', '.puzzle_border', function() {
                $(this).parent().toggleClass('active');
            });

            $('#memberships_switch_view').on('click', function() {
                $rootel.find('.oae-list').toggleClass('grid');
            });
        };

        var doInit = function() {
            addBinding();
            getMemberships();
            $('.memberships_widget').show();
        };

        doInit();
    };
});
