define(['jquery', 'oae.core'], function($, oae) {

    return function(uid, showSettings, widgetData) {

        var $rootel = $('#' + uid);

        // TODO
        var infinityScroll = false;

        /**
         * TODO
         * Render the default template when no results are found. This function will
         * be called by the infinite scroll plugin
         */
        var handleEmptyResultList = function() {
            $('#memberships_create', $rootel).hide();
            oae.api.util.template().render($('#memberships_noresults_template'), null, $('.oae-list', $rootel));
        };

        /**
         * TODO
         * Initializes the infinite scroll plugin that fetches and pages a user's memberships
         */
        var getMemberships = function() {
            // Disable the previous infinite scroll
            if (infinityScroll) {
                infinityScroll.kill();
            }

            var query = $.bbq.getState('q');
            $('.search-query', $rootel).val(query);

            var url = '/api/user/' + widgetData.principalId + '/memberships';
            if (query) {
                url = '/api/search/memberships/' + widgetData.principalId;
            }

            // Set up the infinite scroll for the list of search results
            infinityScroll = $('.oae-list', $rootel).infiniteScroll(url, {
                'limit': 12,
                'q': $.bbq.getState('q')
            }, '#memberships_template', {
                'postProcessor': function(data) {
                    if (widgetData.canManage) {
                        $('#memberships_create', $rootel).show();
                    }

                    // Let the template know whether or not the current list
                    // is a main list or a search list, as different paging
                    // keys need to be provided for each
                    data.isSearch = query ? true : false;
                    return data;
                },
                'emptyListProcessor': handleEmptyResultList
            });
        };

        /**
         * TODO
         */
        var addBinding = function() {
            $rootel.on('submit', '#memberships_search_form', function() {
                $.bbq.pushState({'q': $('.search-query', $(this)).val()});
                getMemberships();
                return false;
            });
        };

        /**
         * TODO
         */
        var doInit = function() {
            addBinding();
            getMemberships();
        };

        doInit();
    };
});
