define(['jquery', 'oae/api/oae.core'], function($, oae) {

    return function(uid, showSettings) {

        var $rootel = $('#' + uid);

        var addBinding = function() {
            $rootel.on('click', '.puzzle_border', function() {
                $(this).parent().toggleClass('active');
            });

            $('#search_switch_view').on('click', function() {
                $rootel.find('.oae-list').toggleClass('grid');
            });

            $('#search_form', $rootel).on('submit', function() {
                doSearch($('#search_input').val());
                return false;
            });
        };

        var renderSearch = function(data) {
            oae.api.util.renderTemplate('#search_template', {
                'results': data.results
            }, $('.oae-list', $rootel));
        };

        var doSearch = function(query) {
            var url = '/api/search/general?&limit=12';
            if (query) {
                url += '&q=' + query;
            }

            $.ajax({
                'url': url,
                'type': 'GET',
                'success': function(data) {
                    renderSearch(data);
                },
                'error': function(jqXHR, textStatus) {
                    console.log({'code': jqXHR.status, 'msg': jqXHR.statusText});
                }
            });
        };

        var doInit = function() {
            addBinding();
            $('.search_widget').show();
        };

        doInit();
    };
});
