define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Renders and shows the terms and conditions container
         */
        var showTermsAndConditions = function() {
            oae.api.util.template().render($('#termsandconditions-template', $rootel), {
                'terms': oae.api.config.getValue('oae-authentication', 'termsAndConditions', 'text').default
            }, $('.modal-body .well', $rootel));
        };

        /**
         * Initializes the edit group modal dialog
         */
        var setUpTermsAndConditions = function() {
            $(document).on('click', '.oae-trigger-termsandconditions', function() {
                $('#termsandconditions-modal', $rootel).modal({
                    'backdrop': 'static'
                });

                showTermsAndConditions();
            });
        };

        setUpTermsAndConditions();

    };
});
